class Device {
    constructor ({model, sid, short_id, data}) {
        this.model = model;
        this.sid = sid;
        this.short_id = short_id;
        this.data = data;
    }

    update (data) {
        for (let key in data) {
            if (this.hasOwnProperty(key)) {
                this[key] = data[key];
            }
        }
    }
}

module.exports = Device;